/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('availablebettypes', {
    oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    odd: {
      type: "DOUBLE",
      allowNull: true
    },
    betresult: {
      type: DataTypes.BOOLEAN,
      allowNull: true
    },
    bettype_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'bettype',
        key: 'oid'
      }
    },
    event_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'event',
        key: 'event_oid'
      }
    }
  }, {
    tableName: 'availablebettypes'
  });
};
