/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('basketballstats', {
    stats_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'stats',
        key: 'oid'
      }
    },
    homepoints: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    awaytriples: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    hometriples: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    awaypoints: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'basketballstats'
  });
};
