/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('bet', {
    oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    wager: {
      type: "DOUBLE",
      allowNull: true
    },
    user_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'user',
        key: 'oid'
      }
    },
    event_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'event',
        key: 'event_oid'
      }
    },
    bettype_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'bettype',
        key: 'oid'
      }
    },
    result_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'result',
        key: 'oid'
      }
    }
  }, {
    tableName: 'bet'
  });
};
