/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('bettype_sport', {
    bettype_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'bettype',
        key: 'oid'
      }
    },
    sport_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'sport',
        key: 'oid'
      }
    }
  }, {
    tableName: 'bettype_sport'
  });
};
