/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('footballstats', {
    stats_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      references: {
        model: 'stats',
        key: 'oid'
      }
    },
    homegoals: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    awaygoals: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    awayyellowcards: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    homeyellowcards: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    awayredcards: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    homeredcards: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'footballstats'
  });
};
