/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('stats', {
    oid: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    gameduration: {
      type: DataTypes.STRING(255),
      allowNull: true
    },
    event_event_oid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'event',
        key: 'event_oid'
      }
    }
  }, {
    tableName: 'stats'
  });
};
